import { userConstants } from '../constants';

let user = JSON.parse(localStorage.getItem('user'));
const initialState = user ? { loggedIn: true, user, token : user.accessTokenResponse.access_token } : {};

export function authentication(state = initialState, action) {
  switch (action.type) {
    case userConstants.LOGIN_REQUEST:
      return {
        loggingIn: true,
        user: action.user
      };
    case userConstants.LOGIN_SUCCESS:
      return {
        loggedIn: true,
        user: action.user
      };
    case userConstants.LOGIN_FAILURE:
      return {};
    case userConstants.LOGOUT:
      return {};
      case userConstants.GET_REFRESH_TOKEN_REQUEST:
      return {
        loggingIn: true,
        token: action.token
      };
    case userConstants.GET_REFRESH_TOKEN_SUCCESS:
      return {
        loggedIn: true,
        token: action.token
      };
    case userConstants.GET_REFRESH_TOKEN_FAILURE:
      return {};
    default:
      return state
  }
}
