import { deliveryConstants } from '../constants';
import { deliveryService } from '../services';
import { alertActions } from '.';
import { history } from '../helpers';

export const deliveryActions = {
    getAll
};

function search() {
    return dispatch => {
        dispatch(request(page, batchSize));

        deliveryService.search(page, batchSize)
            .then(
                deliveries => dispatch(success(deliveries)),
                error => dispatch(failure(error.toString()))
            );
    };

    function request(page, batchSize) { return { type: deliveryConstants.SEARCH_REQUEST, page, batchSize } }
    function success(deliveries) { return { type: deliveryConstants.SEARCH_SUCCESS, deliveries } }
    function failure(error) { return { type: deliveryConstants.SEARCH_FAILURE, error } }
}
