import React from 'react';
import { connect } from 'react-redux';
import { ticketActions } from '../actions';
import { MDBTable, MDBTableBody, MDBTableHead  } from 'mdbreact';

class TicketListView extends React.Component {

    constructor(props) {
        super(props);

        this.state = {
            ticket: []
        }
    }

    componentDidMount() {
        this.props.getAll();
    }

    render() {
        const { ticket } = this.props;

        const data = {
            columns: [
                {
                    label: "Ticket ID",
                    field: 'id',
                    sort: "asc",
                    width: 150
                },
                {
                    label: "Priority",
                    field: "priority",
                    sort: "asc",
                    width: 200
                },
                {
                    label: "Satus",
                    field: "ticketStatus",
                    sort: "asc",
                    width: 250
                }
            ],
            rows: this.props.ticket != null ? this.props.ticket.items : []            
        };

        const columns = [
            {
                label: "Ticket ID",
                field: 'id',
                sort: "asc",
                width: 150
            },
            {
                label: "Priority",
                field: "priority",
                sort: "asc",
                width: 200
            },
            {
                label: "Satus",
                field: "ticketStatus",
                sort: "asc",
                width: 250
            }
        ];

        return (
            <div>
                {ticket.loading && <em>Loading deliveries...</em>}
                {ticket.error && <span className="text-danger">ERROR: {ticket.error}</span>}
                {ticket.items &&

                    <div className="container">
                        <div className="col-md-12">
                            <h2 className="text-center">Online Delivery Business</h2>
                            <h5 className="sign-in-sub-title">Search and monitor for a particular delivery</h5>
                            <br /><br /><br /><br />
                            <div id="data-table">          

                                <MDBTable 
                                small 
                                hover 
                                noBottomColumns 
                                displayEntries={false} 
                                searchLabel=" "
                                barReverse
                                info={false}>
                                    <MDBTableHead columns={columns} />
                                    <MDBTableBody rows={this.props.ticket} />
                                    </MDBTable>
                        </div>
                        </div>
                    </div>

                }
            </div>

        );
    }
}

function mapState(state) {
    const { ticket } = state;
    return { ticket };
}

const actionCreators = {
    getAll: ticketActions.getAll
}

const connectedTicketListView = connect(mapState, actionCreators)(TicketListView);
export { connectedTicketListView as TicketListView };
