import React from 'react';
import { connect } from 'react-redux';
import { ticketActions } from '../actions';
import { MDBDataTable } from "mdbreact";

class TicketListView extends React.Component {

    constructor(props) {
        super(props);

        this.state = {
            delivery: []
        }
    }

    componentDidMount() {
        this.props.getAll();
    }



    componentDidMount() {
        this.props.getAll();
    }


    render() {
        const { ticket } = this.props;

        const data = {
            columns: [
                {
                    label: "Delivery ID",
                    field: "id",
                    sort: "asc",
                    width: 150
                },
                {
                    label: "Customer Type",
                    field: "customerType",
                    sort: "asc",
                    width: 200
                },
                {
                    label: "Delivery Status",
                    field: "deliveryStatus",
                    sort: "asc",
                    width: 200
                },
                {
                    label: "Expected Delivery Time",
                    field: "expectedDeliveryTime",
                    sort: "asc",
                    width: 250
                },
                {
                    label: "Time To Reach Destination",
                    field: "timeToReachDestination",
                    sort: "asc",
                    width: 150
                }
            ],
            rows: this.props.ticket != null ? this.props.ticket.items : []
        };

        return (
            <div>
                {ticket.loading && <em>Loading deliveries...</em>}
                {ticket.error && <span className="text-danger">ERROR: {ticket.error}</span>}
                {ticket.items &&
                    <MDBDataTable striped bordered hover data={data} />
                }
            </div>

        );
    }
}

function mapState(state) {
    const { tickets } = state;
    return { tickets };
}

const actionCreators = {
    getAll: ticketActions.getAll
}

const connectedTicketListView = connect(mapState, actionCreators)(TicketListView);
export { connectedTicketListView as TicketListView };
