import { userConstants } from '../constants';
import { userService } from '../services';
import { alertActions } from './';
import { history } from '../helpers';

export const userActions = {
    login,
    logout
};

function login(username, password) {
    return dispatch => {
        dispatch(request({ username }));

        userService.login(username, password)
            .then(
                user => { 
                    dispatch(success(user));
                    history.push('/');
                },
                error => {
                    dispatch(failure(error.toString()));
                    dispatch(alertActions.error(error.toString()));
                }
            );
    };

    function request(user) { return { type: userConstants.LOGIN_REQUEST, user } }
    function success(user) { return { type: userConstants.LOGIN_SUCCESS, user } }
    function failure(error) { return { type: userConstants.LOGIN_FAILURE, error } }
}

function logout() {
    userService.logout();
    return { type: userConstants.LOGOUT };
}


function getRefreshToken(accessToken) {
    return dispatch => {
        dispatch(request(accessToken));

        userService.getRefreshToken(accessToken)
            .then(
                user => { 
                    dispatch(success(accessToken));
                },
                error => {
                    dispatch(failure(error.toString()));                    
                }
            );
    };

    function request(accessToken) { return { type: userConstants.GET_REFRESH_TOKEN_REQUEST, accessToken } }
    function success(accessToken) { return { type: userConstants.GET_REFRESH_TOKEN_SUCCESS, accessToken } }
    function failure(error) { return { type: userConstants.GET_REFRESH_TOKEN_FAILURE, error } }
}
